ALTER TABLE `campaign_settings` ADD `max_screens` INT(2) NOT NULL DEFAULT '5' AFTER `campaign_code`, ADD `use_snapgrid` TINYINT(1) NOT NULL DEFAULT '1' AFTER `max_screens`, ADD `snapgrid_radius` SMALLINT(3) NOT NULL DEFAULT '5' AFTER `use_snapgrid`;
ALTER TABLE `custom_fields` ADD `is_required` BOOLEAN DEFAULT false AFTER `variables`, ADD `tool_tip` VARCHAR(128) NOT NULL AFTER `is_required`, ADD `place_holder` VARCHAR(128) NOT NULL AFTER `tool_tip`, ADD `css_class` VARCHAR(128) NOT NULL AFTER `place_holder`, ADD `field_name` VARCHAR(128) NOT NULL AFTER `css_class`, ADD `is_hidden` BOOLEAN DEFAULT false AFTER `css_class`, ADD `is_locked` BOOLEAN DEFAULT false AFTER `is_hidden`;